{"thumbs":"","arrows":".wolverine.tparrows {
	cursor:pointer;
	width:40px;
	height:40px;
	position:absolute;
	display:block;
	z-index:100;
    background: transparent;
	border: 1px solid rgba(255,255,255,0.7);
	color: rgba(255,255,255,0.7);
	-webkit-border-radius: 100%;
	-moz-border-radius: 100%;
	border-radius: 100%;
}
.wolverine.tparrows:before {
	font-family: \"revicons\";
	font-size:15px;
	color:#fff;
	display:block;
	line-height: 40px;
	text-align: center;
}
.wolverine.tparrows.tp-leftarrow:before {
	content: \"\\e824\";
}
.wolverine.tparrows.tp-rightarrow:before {
	content: \"\\e825\";
}

","bullets":".wolverine .tp-bullet{
  border-radius: 50%;
  background:transparent;
  width:10px;
  height:10px;
}
.wolverine .tp-bullet.selected,
.wolverine .tp-bullet:hover {
  border:none;
  border-radius: 50%;
   background:transparent;
}



.wolverine .tp-bullet-inner {
  -webkit-transition: background-color 0.3s ease;
  transition: background-color 0.3s ease;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  outline: none;
  border-radius: 50%;
  background-color: #fff;
  background-color: rgba(221, 221, 221, 0.3);
  text-indent: -999em;
  cursor: pointer;
  position: absolute;
}

.wolverine .tp-bullet.selected .tp-bullet-inner,
.wolverine .tp-bullet:hover .tp-bullet-inner{
 background-color: rgba(221, 221, 221, 0.7);
}
